class Animation{constructor(t,i,h,s,r,e,a,c){this.positionMatrix=t,this.img=i,this.x=h,this.offsetY=s,this.characterWidth=r,this.characterHeight=e,this.y=height-this.characterHeight-this.offsetY,this.spriteWidth=a,this.spriteHeight=c,this.currentFrame=0}display(){image(this.img,this.x,this.y,this.characterWidth,this.characterHeight,this.positionMatrix[this.currentFrame][0],this.positionMatrix[this.currentFrame][1],this.spriteWidth,this.spriteHeight)}animate(){this.currentFrame++,this.currentFrame>this.positionMatrix.length-1&&(this.currentFrame=0)}stopAnimation(){this.currentFrame=0}}