const initialBpm=0,intermediateBpm=40,highBpm=70,veryHighBpm=85,maxBpm=100,initialFirstAid=1,maxFirstAid=1,colorGreen="#008000",colorYellow="#F9B100",colorRed="#8A0707";class Life{constructor(i){this.imgFirstAid=i,this.bpm=0,this.maxBpm=100,this.firstAid=1,this.maxFirstAid=1,this.firstAidHasDecreased=!1,this.imgFirstAidX=150,this.imgFirstAidY=10,this.imgFirstAidWidth=30,this.imgFirstAidHeight=25,this.imgStress=imgStressGreen,this.textSize=20}display(){this.displayBpm(),this.displayFirstAid()}increaseBpm(){if(this.textSize=30,this.bpm<=100){let i=5;this.bpm>80&&this.bpm<=90?i=3:this.bpm>90&&(i=2);let t=Math.floor(Math.random()*i)+1;this.bpm+t<=100?this.bpm=this.bpm+t:this.bpm=100}else this.bpm=100}resetBpm(){this.bpm=0,this.firstAidHasDecreased=!1}decreaseBpm(i){null==i?this.bpm=Math.floor(this.bpm/2):this.bpm-i>0?this.bpm=this.bpm-i:this.bpm=0}increaseFirstAid(){this.firstAid<1&&this.firstAid++}decreaseFirstAid(){return!(this.firstAid>0)||(this.firstAid--,!1)}resetFirstAid(){this.firstAid=1}displayBpm(){image(this.imgStress,20,5),this.bpm<=40?(this.imgStress=imgStressGreen,P5Style.bpmCountStyle("#008000",this.textSize)):this.bpm<=70?(this.imgStress=imgStressYellow,P5Style.bpmCountStyle("#F9B100",this.textSize)):this.bpm<=85?(this.imgStress=imgStressRed,P5Style.bpmCountStyle(colorRed,this.textSize)):(this.imgStress=imgStressBomb,P5Style.bpmCountStyle(colorRed,this.textSize)),text(this.bpm,70,30),this.textSize=20}displayFirstAid(){image(this.imgFirstAid,this.imgFirstAidX,this.imgFirstAidY,this.imgFirstAidWidth,this.imgFirstAidHeight),P5Style.firstAidCountStyle(),text(this.firstAid+"/1",this.imgFirstAidX+40,30)}}