class FloatingMessage{constructor(a,b,c,d,e,f){this.textMessage=a,this.originalSpeed=b,this.speed=b,this.x=c,this.y=d,this.boxWidth=e,this.boxHeight=f}display(){fill(255,255,255,200),noStroke(),rect(this.x,this.y,this.boxWidth,this.boxHeight),P5Style.clockCountStyle(),text(this.textMessage,this.x+10,this.y+10,this.boxWidth,this.boxHeight)}move(){this.x-=this.speed}stop(){this.speed=0}restart(){this.speed=this.originalSpeed,this.move()}}