class PowerUp{constructor(i,t,s,e,h,p,r){this.img=i,this.x=t,this.y=s,this.speed=e,this.originalSpeed=e,this.powerUpWidth=h,this.powerUpHeight=p,this.type=r,this.characterWidth=h,this.characterHeight=p,this.visible=!0,this.picked=!1}display(){this.visible&&image(this.img,this.x,this.y,this.powerUpWidth,this.powerUpHeight)}move(){this.x=this.x-this.speed}stop(){this.speed=0}restart(){this.speed=this.originalSpeed,this.move()}getPicked(){powerUpTheme.play(),this.picked=!0,this.visible=!1}}