class Scenario{constructor(a,b,c){this.myImage=a,this.originalSpeed=b,this.speed=b,this.imgScenarioWidth=c,this.x1=0,this.x2=c}display(){image(this.myImage,this.x1,0,this.imgScenarioWidth,height),image(this.myImage,this.x2,0,this.imgScenarioWidth,height)}move(){this.x1-=this.speed,this.x2-=this.speed,this.x1<-this.imgScenarioWidth&&(this.x1=this.imgScenarioWidth),this.x2<-this.imgScenarioWidth&&(this.x2=this.imgScenarioWidth)}stop(){this.speed=0}restart(){this.speed=this.originalSpeed,this.move()}}